import React, {Component} from 'react';
import PostData from './post.json';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import { Redirect } from 'react-router';
//import { browserHistory } from 'react-router';
class Postlist extends Component{

constructor(props){
    super(props);
    this.state={
        data: PostData,
        redirect : false
      }
      this.delete = this.delete.bind(this);
    }

delete(event){   //event isliye jisse jahan hum click krte hai wo element access ho jata hai...
  var id = event.target.id;
  var data1 = this.state.data;
    data1.splice(id,1);
    this.setState({
        data : data1
      })
}

open(event){
  var id = event.target.id;
  //console.log(id);
  //this.context.router.history.push("/some/Path");
  // browserHistory.push('/id');
  <Route path="/user/:id" component={Postshow} />;
// this.setState({
//   redirect: true
//       });
}

render() {
    return (
      // if (this.state.redirect) {
      //       return <Route path="/user/:Postdetails.id" component={Postshow} />;
      // }
      <div>
        <h1>todo json App </h1>
        {PostData.map((Postdetails,index)=>{
            return <div key={index}>
                        <h2 onClick={this.open.bind(this)} id={index}>{Postdetails.title} <span onClick={this.delete} id={index}>X</span></h2>
                        <p>{Postdetails.content}</p>
                  </div>
          })}
  </div>
    )
  }
}

export default Postlist;
