import React, {Component} from 'react';
import PostData from './post.json';

class Postlist extends Component{

constructor(props){
    super(props);
    this.state={
        data: PostData
      }
    //  this.delete = this.delete.bind(this);
}

delete(id){   //event isliye jisse jahan hum click krte hai wo element access ho jata hai...

  // var id = event.target.id;
  var data1 = this.state.data;
    data1.splice(id,1);
    this.setState({
        data : data1
      })

}

render() {
    return (
      <div>
        <h1>todo json App </h1>
        {PostData.map((Postdetails,index)=>{
            return <div key={index}> /*yeh chiz isliye kri humne kyunki hume btana tha ki hr div ko */
                        <h2 >{Postdetails.title} <span onClick={this.delete.bind(this, index)} id={index}>X</span></h2>
                        <p>{Postdetails.content}</p>
                  </div>
          })}
      </div>
    )
  }
}

export default Postlist;
