import React, {Component} from 'react';
import PostData from './post.json';

class Postlist extends Component{

onClick(event){
    var id = event.target.id;
    console.log(id);
}


render() {
    return (
      <div>
        <h1>todo json App </h1>
        {PostData.map((Postdetais,index)=>{
            return <div>
                      <h1>{Postdetais.title} <span onClick={this.onClick} id={index}>X</span></h1>
                      <p>{Postdetais.content}</p>
                  </div>
          })}
      </div>
    )
  }
}

export default Postlist;
