import React, {Component} from 'react';
import PostData from './post.json';

class Postlist extends Component{


//var obj = JSON.parse(PostData);


delete(event){
    var id = event.target.id;
  //  var posttodoinstance = this.state.posttodoItems;
  //  Postdetails.splice(id,1);
    //this.setState({
      //  posttodoItems : posttodoinstance
      //})
      var parent = document.getElementById('title');
      var child = document.getElementById('t1');
      parent.removeChild(child);
      //console.log(id);
}


render() {
    return (
      <div>
        <h1>todo json App </h1>
        {PostData.map((Postdetails,index)=>{
            return <div>
                    <div id="title">
                      <h2 id="t1">{Postdetails.title} <span onClick={this.delete} id={index}>X</span></h2>
                      <p>{Postdetails.content}</p>
                    </div>
                  </div>
          })}
      </div>
    )
  }
}

export default Postlist;
