import React, {Component} from 'react';
import PostData from './post.json';

class Postlist extends Component{

constructor(props){
    super(props);
    this.state={
        data: PostData
      }
      this.delete = this.delete.bind(this);
}

delete(event){   //event isliye jisse jahan hum click krte hai wo element access ho jata hai...
  var id = event.target.id;
  var data1 = this.state.data;
    data1.splice(id,1);
    this.setState({
        data : data1
      })
}

render() {
    return (
      <div>
        <h1>todo json App </h1>
        {!showDetails && PostData.map((Postdetails,index)=>{
            return <div key={index}>
                        <h2 onClick={this.open}>{Postdetails.title} <span onClick={this.delete} id={index}>X</span></h2>
                        <p>{Postdetails.content}</p>
                  </div>
          })}
      </div>
    )
  }
}

export default Postlist;
