import React, {Component} from 'react';

import PostData from './post.json';

import { Link } from "react-router-dom";

//import './App.css';

//import { Redirect } from 'react-router';

//import { browserHistory } from 'react-router';

class Postlist extends Component{



constructor(props){

    super(props);

    this.state={

        data: PostData,
        expanded : false,
        ids:null,
      }

    }



delete(event){   //event isliye jisse jahan hum click krte hai wo element access ho jata hai...

  var id = event.target.id;

  var data1 = this.state.data;

    data1.splice(id,1);

    this.setState({

        data : data1

      })

}

open(event){   //event isliye jisse jahan hum click krte hai wo element access ho jata hai...

  var id = event.target.id;
//console.log(id);
  this.setState({
        expanded : true,
        ids:id
      })

}

goBack(){
  this.setState({
        expanded : false,
        ids:null,
      })
}



render() {
const ex= this.state.expanded;

const id = this.state.ids;
if(!ex)
{
    return (
      <ul>
      <li>
        <h1 className="header">Click to view detail</h1>
          {PostData.map((Postdetails,index)=>{
            return <div className="body" key={index}>
                        <h2 id={index} onClick={this.open.bind(this)}>{Postdetails.title}</h2>
                        <p>{Postdetails.content}</p>
                        <span onClick={this.delete.bind(this)} id={index}>X</span>
                  </div>

            })}
        </li>
        </ul>
    );
}
else
{
    return (
      <div>
          <button onClick={this.goBack.bind(this)}>back</button>
          <p>{PostData[id].content}</p>
      </div>
    );
}
}

}



export default Postlist;
