import React, {Component} from 'react';
import PostData from './post.json';


class Postlist extends Component{

constructor(props){
    super(props);
    this.state={
        data: PostData,
        expanded : false,
        ids:null,
      }
}


delete(id){
  var data1 = this.state.data;
    data1.splice(id,1);
    this.setState({
        data : data1
      })
}


open(id){
  this.setState({
        expanded : true,
        ids:id
      })
}


goBack(){
  this.setState({
        expanded : false,
        ids:null,
      })
}

render() {
              const ex= this.state.expanded;
              const id = this.state.ids;
              return (
                <div>
                  {!ex &&
                    <ul>
                      <h1 className="header">Click to view detail</h1>
                        {PostData.map((Postdetails,index)=>{
                          return <li key={index}>
                                      <div className="body"  onClick={() => this.open(index)}>
                                        <h2>{Postdetails.title}</h2>
                                        <p>{Postdetails.content}</p>
                                      </div><span onClick={() => this.delete(index)}>X</span>
                                 </li>
                          })}
                     </ul>
                  }
                  {ex &&  <div className="detailsheader">
                        <button onClick={() => this.goBack()}>back</button>
                        <h2>{PostData[id].title}</h2>
                        <p>{PostData[id].content}<br/><br/>
                            {PostData[id].date}<br/><br/>
                            {PostData[id].slug}
                        </p>
                    </div>
                  }
                </div>
              )
        }
}


export default Postlist;
