import React, {Component} from 'react';
import PostData from './post.json';


class Postlist extends Component{

constructor(props){
    super(props);
    this.state={
        data: PostData,
        expanded : false,
        ids:null,
      }
}


delete(event){
  var id = event.target.id;
  var data1 = this.state.data;
    data1.splice(id,1);
    this.setState({
        data : data1
      })
}


open(event){
  var id = event.target.id;
  this.setState({
        expanded : true,
        ids:id
      })
}


goBack(){
  this.setState({
        expanded : false,
        ids:null,
      })
}



render() {
              const ex= this.state.expanded;
              const id = this.state.ids;
              if(!ex)
              {
                  return (
                    <ul>
                      <h1 className="header">Click to view detail</h1>
                        {PostData.map((Postdetails,index)=>{
                          return <li><div className="body" key={index} onClick={(e) => this.open(e)}>
                                        <h2 id={index}>{Postdetails.title}</h2>
                                        <p id={index}>{Postdetails.content}</p>
                                      </div><span onClick={(e) => this.delete(e)} id={index}>X</span>
                                 </li>
                          })}
                     </ul>
                  );
              }
              else
              {
                  return (
                    <div className="detailsheader">
                        <button onClick={() => this.goBack()}>back</button>
                        <h2>{PostData[id].title}</h2>
                        <p>{PostData[id].content}<br/><br/>
                            {PostData[id].date}<br/><br/>
                            {PostData[id].slug}
                        </p>
                    </div>
                  );
              }
          }
}

export default Postlist;
