      this.setState({
        showdetails : true,
        data : data1
    })
}*/

render() {
    return (
      <div>
        <h1>todo json App </h1>
        var linklist = {PostData.map((Postdetails,index)=>{
            return(
                <li>
                  <Link to={`${match.url}/${PostData.id}`}>
                        <div key={index}>
                        <h2 onClick={this.open} id={index}>{Postdetails.title} <span onClick={this.delete} id={index}>X</span></h2>
                        <p>{Postdetails.content}</p>
                        </div>
                  </Link>
                </li>
                )
            })
          }return
      <div>
        <div>
         <div>
            <ul> {linkList} </ul>
         </div>
        </div>
        <Route path={`${match.url}/:productId`}
            render={ (props) => <Product data= {PostData} {...props} />}/>
              <Route exact path={match.url}
                render={() => (
                    <div>Please select a product.</div>
                  )}
                />
      </div>
      }

      </div>
    )
  }
}

export default Postlist;
