import React, {Component} from 'react';
import PostData from './post.json';
import { Link } from "react-router-dom";
//import './App.css';
//import { Redirect } from 'react-router';
//import { browserHistory } from 'react-router';
class Postlist extends Component{
constructor(props){
super(props);
this.state={
data: PostData,
expanded : false,
        id:null,
}
}
delete(event){ //event isliye jisse jahan hum click krte hai wo element access ho jata hai...
var id = event.target.id;
var data1 = this.state.data;
data1.splice(id,1);
this.setState({
data : data1
})
}
open(index){
var id = index;
this.setState({
    expanded : !this.state.expanded ,
    id:id
  })
}

render() {
const ex= this.state.expanded;
let id = this.state.id ;
return (
<div>
        { ex &&<div>
          <h1 className="header">Click to view detail</h1>
{PostData.map((Postdetails,index)=>{
return <div className="body" key={index}>
<ul>
<li>
<div onclick={() => this.open(index)}>
                          <h2>{Postdetails.title}</h2>
                          <p>{Postdetails.content}</p>
</div>
<span onClick={() => this.delete} id={index}>X</span>
</li>
</ul>
</div>
})}

        </div> }
        { !ex &&<div>
              <h2>{this.state.PostData[id].title}</h2>
<p>{this.state.PostData[id].content}<br/><br/>
{this.state.PostData[id].date}<br/><br/>
{this.state.PostData[id].slug}
</p>
          </div> }
      </div>
)
}
}

export default Postlist;
