import React, {Component} from 'react';
import PostData from './post.json';
import { BrowserRouter as Router, Route, Link } from "react-router-dom";





const Products = ({ match }) => {


     const productsData = [
      {
          id: 1,
          title: 'Wake up',
          content: "Try Reactjs is awesome. Can't wait to learn more",
          date: "w12-04-2017",
          slug: "hello-world"
      },
      {
          "id": 2,
          "title": "Take breakfast",
          "content": "Setting up react is easy. Learn more about it!",
          "date": "b1-28-2018",
          "slug": "setup-react"
      },
      {
          "id": 3,
          "title": "Go to Office",
          "content": "tomato up react is easy. Learn more about it!",
          "date": "t1-28-2018",
          "slug": "setup-react"
      }
  ];


  /* const productsData = [
    {
        id: 1,
        name: 'NIKE Liteforce Blue Sneakers',
        description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Proin molestie.',
        status: 'Available'

    }

    //Rest of the data has been left out for code brevity

];*/
 //Create an array of `<li>` items for each product
  var linkList = productsData.map( (product) => {
    return(
      <li>
        <Link to={`${match.url}/${product.id}`}>
          {product.name}
        </Link>
      </li>
      )

    })

  return(
    <div>
        <div>
         <div>
           <h3> Products</h3>
           <ul> {linkList} </ul>
         </div>
        </div>

        <Route path={`${match.url}/:productId`}
            render={ (props) => <Products data= {productsData} {...props} />}/>
        <Route exact path={match.url}
            render={() => (
            <div>Please select a product.</div>
            )}
        />
    </div>
  )
}
export default Products;
